import { Observable } from 'rxjs';
import { Product } from '../Model/Product';
import { ProductService } from '../Services/product.service';
import { Component, OnInit } from '@angular/core';
@Component({
  selector: 'app-product-show-all',
  templateUrl: './product-show-all.component.html',
  styleUrls: ['./product-show-all.component.css'],
})
export class DisplayProductsComponent implements OnInit {
  prdlist: Product[];

  constructor(private productService: ProductService) {}

  ngOnInit() {
    this.getAllProducts();
  }

  getAllProducts() {
    this.productService.getAllProducts().subscribe(
      (data) => {
        this.products = data;
      },
      (error) => {
        console.log(error);
      }
    );
  }
}
<table
  class="table table-striped table-bordered bg-info text-dark"
  style="width: 70%; margin-left: 100px"
>
  <thead>
    <th>Id</th>
    <th>Name</th>
    <th>Cost</th>
  </thead>
  <tbody>
    <tr *ngFor="let p of prdlist | async">
      <td>{{ p.id }}</td>
      <td>{{ p.name }}</td>
      <td>{{ p.cost }}</td>
    </tr>
  </tbody>
</table>
