// Import the Injectable decorator from the Angular core
import { Injectable } from '@angular/core';

// Import the HttpClient module to make HTTP requests
import { HttpClient } from '@angular/common/http';

// Import the Observable class from the rxjs library to handle asynchronous data streams
import { Observable } from 'rxjs';

@Injectable({
  // The providedIn property specifies that this service should be available at the root level,
  // meaning it will be a singleton service accessible throughout the application.
  providedIn: 'root',
})
export class ProductService {
  constructor(private http: HttpClient) {
    // The constructor is a special method that gets executed when an instance of this service is created.
    // Here, we inject the HttpClient into the ProductService using dependency injection.
    // This allows us to use the HttpClient to make HTTP requests to the server.
  }

  getAll(): Observable<any> {

    return this.http.get('http://localhost:9090/Product/all');
  }
}
