import { Component } from '@angular/core';
import { Product } from '../product-show-all/Model/Product';
import { ProductService } from '../Services/product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css'],
})
export class AddProductComponent {
  prd: Product;

  constructor(private service: ProductService, router: Router) {
    this.prd = new Product();
  }

  addProuct() {
    this.service
  }
}
<p>add-product works!</p>

<div class="container">
  <h1>Product Form</h1>

  <form>
    <div class="form-group">
      <label>Enter Id</label>

      <input class="form-control" />
    </div>

    <div class="form-group">
      <label>Enter Name</label>

      <input class="form-control" />
    </div>

    <div class="form-group">
      <label>Enter Cost</label>

      <input class="form-control" />
    </div>

    <div>
      <button type="submit" class="btn btn-primary">Add Product</button>

      <button type="reset" class="btn btn-primary">Cancel Product</button>
    </div>
  </form>
</div>
