import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ProductShowAllComponent } from './product-show-all/product-show-all.component';
import { AddProductComponent } from './add-product/add-product.component';

const routes: Routes = [
  { path: 'product-list', component: ProductShowAllComponent },
  { path: 'add-product', component: AddProductComponent },
  // Add other routes if needed
  { path: '', redirectTo: '/product-list', pathMatch: 'full' }, // Default route
  { path: '**', redirectTo: '/product-list' }, // Redirect unknown paths to the product-list
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule],
})
export class AppRoutingModule {}
