import { Component, OnInit } from '@angular/core';
import { Product } from '../Model/Product';
import { ProductService } from '../Services/product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css'],
})
export class AddProductComponent implements OnInit {
  newProduct: Product = {
    productID: 0,
    productName: '',
    productCost: 0,
  };

  constructor(private productService: ProductService) {}

  ngOnInit() {}

  addProduct() {
    this.productService.addProduct(this.newProduct).subscribe(
      (data) => {
        console.log('Product added successfully:', data);
        // Clear the form after successful addition
        this.newProduct = {
          productID: 0,
          productName: '',
          productCost: 0,
        };
      },
      (error) => {
        console.log('Error adding product:', error);
      }
    );
  }
}
<div class="container">
  <h1>Product Form</h1>
  <form (ngSubmit)="addProduct()">
    <div class="form-group">
      <label>Enter Id</label>
      <input class="form-control" [(ngModel)]="prd.id" name="prd.id" />
    </div>

    <div class="form-group">
      <label>Enter Name</label>
      <input class="form-control" [(ngModel)]="prd.name" name="prd.name" />
    </div>
    <div class="form-group">
      <label>Enter Cost</label>
      <input class="form-control" [(ngModel)]="prd.cost" name="prd.cost" />
    </div>
    <div>
      <button
        type="submit"
        class="btn btn-primary mt-2 mb-5"
        style="margin-right: 20px"
      >
        Add Product
      </button>
      <button type="reset" class="btn btn-primary mt-2 mb-5">
        Cancel Product
      </button>
    </div>
  </form>
</div>
